// Extrair títulos e links de notícias do HTML
const html = $input.first().json.data;
const cheerio = require('cheerio');
const $ = cheerio.load(html);

const articles = [];

// Seletores específicos para cada site
if ($input.first().json.url?.includes('orthobuzz')) {
  $('.post-title a, .entry-title a, h2 a, h3 a').each((i, el) => {
    const title = $(el).text().trim();
    const link = $(el).attr('href');
    if (title && link) {
      articles.push({ title, link, source: 'OrthoBuzz' });
    }
  });
} else if ($input.first().json.url?.includes('orthofeed')) {
  $('.post-title a, .entry-title a, h2 a, h3 a').each((i, el) => {
    const title = $(el).text().trim();
    const link = $(el).attr('href');
    if (title && link) {
      articles.push({ title, link, source: 'OrthoFeed' });
    }
  });
} else if ($input.first().json.url?.includes('massdevice')) {
  $('.entry-title a, .post-title a, h2 a, h3 a').each((i, el) => {
    const title = $(el).text().trim();
    const link = $(el).attr('href');
    if (title && link) {
      articles.push({ title, link, source: 'MassDevice' });
    }
  });
} else if ($input.first().json.url?.includes('reuters')) {
  ('[data-testid="Heading"] a, .story-title a, h3 a').each((i, el) => {
    const title = $(el).text().trim();
    const link = $(el).attr('href');
    if (title && link) {
      articles.push({ title, link: link.startsWith('http') ? link : 'https://reuters.com' + link, source: 'Reuters Health' });
    }
  });
} else if ($input.first().json.url?.includes('techcrunch')) {
  ('.post-block__title__link, h2 a, h3 a').each((i, el) => {
    const title = $(el).text().trim();
    const link = $(el).attr('href');
    if (title && link) {
      articles.push({ title, link, source: 'TechCrunch Health' });
    }
  });
} else if ($input.first().json.url?.includes('medicalfuturist')) {
  ('.post-title a, .entry-title a, h2 a, h3 a').each((i, el) => {
    const title = $(el).text().trim();
    const link = $(el).attr('href');
    if (title && link) {
      articles.push({ title, link, source: 'Medical Futurist' });
    }
  });
}

return articles.slice(0, 5); // Limitar a 5 artigos por fonte

