  'LLY': 'Eli Lilly and Company',
  'ABBV': 'AbbVie Inc.',
  'NVS': 'Novartis AG',
  'AZN': 'AstraZeneca PLC',
  'MRK': 'Merck & Co.',
  'NVO': 'Novo Nordisk A/S',
  'BSX': 'Boston Scientific',
