  'SYK': 'Stryker Corporation',
  'SNN': 'Smith & Nephew',
  'OFIX': 'Orthofix Medical',
  'ZBH': 'Zimmer Biomet',
  'GMED': 'Globus Medical',
  'KIDS': 'OrthoPediatrics',
  'RDOR3.SA': 'Rede D\'Or São Luiz',
  'RADL3.SA': 'Raia Drogasil',
  'ONCO3.SA': 'Oncoclínicas',
  'HAPV3.SA': 'Hapvida UnitedHealth',
  'FLRY3.SA': 'Fleury',
  'DASA3.SA': 'Dasa',
  'QUAL3.SA': 'Qualicorp'
};

// Processar todos os nodes com Yahoo Finance
for (const config of nodeConfig) {
  try {
    const nodeData = $(config.name).first().json;

    // Processar dados do Yahoo Finance
    if (nodeData.chart && nodeData.chart.result && nodeData.chart.result[0]) {
      const result = nodeData.chart.result[0];
      const meta = result.meta;

      const currentPrice = meta.regularMarketPrice;
      const previousClose = meta.previousClose;
      const change = currentPrice - previousClose;
      const changePercent = (change / previousClose) * 100;

      // Determinar moeda baseada no ticker
      const currency = config.ticker.includes('.SA') ? 'BRL' : 'USD';

      allStockData.push({
        json: {
          ticker: config.ticker,
          companyName: companyNames[config.ticker] || meta.longName || meta.shortName || config.ticker,
          currentPrice: currentPrice.toFixed(2),
          previousClose: previousClose.toFixed(2),
          change: change.toFixed(2),
          changePercent: changePercent.toFixed(2),
          currency: currency,
          volume: meta.regularMarketVolume || 0,
          source: 'Yahoo Finance'
        }
      });
    } else {
      // Yahoo sem dados
      allStockData.push({
        json: {
          ticker: config.ticker,
          companyName: companyNames[config.ticker] || config.ticker,
          currentPrice: '0.00',
          previousClose: '0.00',
          change: '0.00',
          changePercent: '0.00',
          currency: config.ticker.includes('.SA') ? 'BRL' : 'USD',
          volume: 0,
          source: 'Yahoo Finance',
          error: 'Sem dados disponíveis'
        }
      });
    }
  } catch (error) {
    console.log(`Erro ao processar ${config.name} (${config.ticker}):`, error);
    allStockData.push({
      json: {
        ticker: config.ticker,
        companyName: companyNames[config.ticker] || config.ticker,
        currentPrice: '0.00',
        previousClose: '0.00',
        change: '0.00',
        changePercent: '0.00',
        currency: config.ticker.includes('.SA') ? 'BRL' : 'USD',
        volume: 0,
        source: 'Yahoo Finance',
        error: 'Erro de processamento'
      }
    });
  }
}

return allStockData;

