    datasets: [{
      label: 'Preço Atual (USD)',
      data: validStocks.map(stock => parseFloat(stock.currentPrice)),
      backgroundColor: [
        '#FF6384', '#36A2EB', '#FFCE56', '#4BC0C0', 
        '#9966FF', '#FF9F40', '#FF6384', '#C9CBCF',
        '#4BC0C0', '#FF6384', '#36A2EB', '#FFCE56'
      ]
    }]
  },
  marketCapComparison: {
    labels: validStocks.filter(s => s.marketCap).map(stock => stock.ticker),
    datasets: [{
      label: 'Market Cap (Bilhões USD)',
      data: validStocks.filter(s => s.marketCap).map(stock => (stock.marketCap / 1000000000).toFixed(2)),
      backgroundColor: '#36A2EB'
    }]
  },
  dailyChanges: {
    labels: validStocks.map(stock => stock.ticker),
    datasets: [{
      label: 'Mudança Diária (%)',
      data: validStocks.map(stock => parseFloat(stock.changePercent)),
      backgroundColor: validStocks.map(stock => 
        parseFloat(stock.changePercent) >= 0 ? '#4BC0C0' : '#FF6384'
      )
    }]
  }
};

return { chartData, stockData: validStocks };

